import React, { useEffect, useState } from "react"
import PokemonListItem from "./PokemonDexInfo"

export default function PokemonInfo({ location }) {
  const { id, name, url } = location.state
  const [pokemonInfo, setPokemonInfo] = useState({})
  const [isPokemonInfoLoaded, setIsPokemonInfoLoaded] = useState(false)
  const [pokemonDexInfo, setPokemonDexInfo] = useState({})
  const [isPokemonDexInfoLoaded, setIsPokemonDexInfoLoaded] = useState(false)
  const [errorInfo, setErrorInfo] = useState(null)
  const [errorDex, setErrorDex] = useState(null)
  console.log(id, name, url);

  /*

  */

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then(
        (data) => {
          setIsPokemonInfoLoaded(true)
          setPokemonInfo(data)
        },
        (error) => {
          setIsPokemonInfoLoaded(true)
          setErrorInfo(error)
        }
      )
  }, [])

  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon-species/"+id)
      .then((res) => res.json())
      .then(
        (data) => {
          setIsPokemonDexInfoLoaded(true)
          setPokemonDexInfo(data)
        },
        (error) => {
          setIsPokemonDexInfoLoaded(true)
          setErrorDex(error)
        }
      )
  }, [])


  if (!isPokemonInfoLoaded && !isPokemonDexInfoLoaded) {
    return (
      <div className="text-center">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    )
  } 

  else {
    return (
    <div className="container text-center">
      <h1>{name}</h1>
      <p>Numero de dex del pokemon: {id}</p>
      <p>Experiencia base: {pokemonInfo.base_experience}</p>
      <p>Altura: {pokemonInfo.height}</p>
      <p>Tipos: {pokemonInfo.types[0]}</p>

      <h1>Sprites</h1>

      <div className="row row-cols-1 row-cols-md-2">

        <div className="col">
          <h2> Normal </h2>
          <img
            className="card-img-top"
            src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`}
            alt={name}
          />
        </div>

        <div className="col">
          <h2> Shiny </h2>
          <img
            className="card-img-top"
            src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/shiny/${id}.png`}
            alt={name}
          />
        </div>

      </div>
    </div>
    )  
  }


}
