import React, { useEffect, useState } from "react"

export default function PokemonInfo({ location }) {
  const { id, name, url } = location.state
  const [pokemonInfo, setPokemonInfo] = useState({})
  const [isPokemonInfoLoaded, setIsPokemonInfoLoaded] = useState(false)
  const [pokemonDexInfo, setPokemonDexInfo] = useState({})
  const [isPokemonDexInfoLoaded, setIsPokemonDexInfoLoaded] = useState(false)
  const [error, setError] = useState(null)
  console.log(id, name, url);

  /*
    "base_experience": data.base_experience,
            "height": data.height,
            "sprites": {
              "front_default": data.sprites.front_default,
              "front_shiny": data.sprites.front_shiny,
              "brack_default": data.sprites.back_default,
              "back_shiny": data.sprites.back_shiny
            },
            "types": data.types
  */

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then(
        (data) => {
          setIsPokemonInfoLoaded(true)
          setPokemonInfo(data)
        },
        (error) => {
          setIsPokemonInfoLoaded(true)
          setError(error)
        }
      )
  }, [])

  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon-species/495"+id)
      .then((res) => res.json())
      .then(
        (data) => {
          setIsPokemonDexInfoLoaded(true)
          setPokemonDexInfo(data)
        },
        (error) => {
          setIsPokemonDexInfoLoaded(true)
          setError(error)
        }
      )
  }, [])

  if (isPokemonInfoLoaded) {
    console.log("*********** INFO")
    console.log(pokemonInfo)
    console.log("***********")
  }

  if (isPokemonDexInfoLoaded) {
    console.log("*********** DEX ")
    console.log(pokemonDexInfo)
    console.log("***********")
  } 

  return (
    <div className="container">
      <h1>{name}</h1>
      <p>Numero de dex del pokemon: {id}</p>
      <p>
        Endpoint original:
        <a href={url}> {url}</a>
      </p>
      <p>
        La info del dex esta en este endpoint:
        <a href={`https://pokeapi.co/api/v2/pokemon-species/${id}`}>
          {" "}
          {`https://pokeapi.co/api/v2/pokemon-species/${id}`}
        </a>
      </p>
      <p>
        Hacer que este componente muestre la informaci√≥n del pokemon, su info
        del pokedex y el o los tipos ademas de su sprite
      </p>
      <p>Se pueden hacer subcomponentes para mostrar la info</p>
    </div>
  )
}
